mapper.select:
    SELECT
      acct.acct_id,
      acct.acct_title,
      acct.acct_password,
      acct. NAME,
      acct.mobile_phone,
      acct.email,
      acct.is_delete,
      acct.status,
      acct.o_info_id,
      acct.acct_type,
      arr.rela_id,
      ROLE.role_id,
      ROLE.role_title,
      (select o.ORG_NAME from org_info o where o.id=acct.O_INFO_ID) as orgTitle
    FROM
    acct_info acct
    LEFT JOIN acct_role_real arr
    ON acct.ACCT_ID = arr.ACCT_ID
    LEFT JOIN role_info role
    ON arr.ROLE_ID = role.ROLE_ID
    where 1=1 and acct.is_delete = 0
       AND ENT_ID = #{entId} 

       LOGIN_SOURCE = #{loginSource}, 

       AND ACCT_TITLE LIKE '%' || #{acctTitle} ||'%'

       AND ACCT_PASSWORD = #{acctPassword} 

       AND ACCT_CODE = #{acctCode} 

       AND NAME = #{name} 

       AND DEPT = #{dept} 

       AND MOBILE_PHONE = #{mobilePhone} 

       AND EMAIL = #{email} 

       AND REGISTER_TIME = #{registerTime} 

       AND LAST_LOGIN_TIME = #{lastLoginTime} 

       AND IS_DELETE = #{isDelete} 

       AND UPDATED_BY = #{updatedBy} 

       AND UPDATED_TIME = #{updatedTime} 

       AND STATUS = #{status} 

       AND START_TIME = #{startTime} 

       AND ENT_TIME = #{entTime} 

       AND A_COMPANY_ID = #{aCompanyId} 

       AND O_INFO_ID = #{oInfoId} 

       AND O_INFO_NAME = #{oInfoName} 

       AND ACCT_TYPE = #{acctType}

    SELECT
    *
    FROM
    acct_info acct
    LEFT JOIN acct_role_real arr
    ON acct.ACCT_ID = arr.ACCT_ID
    LEFT JOIN role_info role
    ON arr.ROLE_ID = role.ROLE_ID
    where 1=1 and acct.is_delete = 0
     AND ENT_ID = #{entId,jdbcType=BIGINT} 

     LOGIN_SOURCE = #{loginSource,jdbcType=VARCHAR}, 

     AND ACCT_TITLE LIKE '%' || #{acctTitle} ||'%' 

     AND ACCT_PASSWORD = #{acctPassword,jdbcType=VARCHAR} 

     AND ACCT_CODE = #{acctCode,jdbcType=VARCHAR} 

     AND NAME = #{name,jdbcType=VARCHAR} 

     AND DEPT = #{dept,jdbcType=VARCHAR} 

     AND MOBILE_PHONE = #{mobilePhone,jdbcType=VARCHAR} 

     AND EMAIL = #{email,jdbcType=VARCHAR} 

     AND REGISTER_TIME = #{registerTime,jdbcType=TIMESTAMP} 

     AND LAST_LOGIN_TIME = #{lastLoginTime,jdbcType=TIMESTAMP} 

     AND IS_DELETE = #{isDelete,jdbcType=TINYINT} 

     AND UPDATED_BY = #{updatedBy,jdbcType=VARCHAR} 

     AND UPDATED_TIME = #{updatedTime,jdbcType=TIMESTAMP} 

     AND STATUS = #{status,jdbcType=TINYINT} 

     AND START_TIME = #{startTime,jdbcType=TIMESTAMP} 

     AND ENT_TIME = #{entTime,jdbcType=TIMESTAMP} 

     AND A_COMPANY_ID = #{aCompanyId,jdbcType=BIGINT} 

     AND O_INFO_ID like CONCAT(#{oInfoId,jdbcType=VARCHAR},'%') 

     AND O_INFO_NAME = #{oInfoName,jdbcType=BIGINT} 

     AND ACCT_TYPE = #{acctType,jdbcType=BIGINT} 

     AND acct.ACCT_ID = #{acctId}

    SELECT
    acct.acct_id,
    acct.acct_title,
    acct.acct_password,
    acct. NAME,
    acct.mobile_phone,
    acct.email,
    acct.is_delete,
    acct.status,
    acct.o_info_id,
    acct.acct_type,
    o.org_name AS orgtitle
    FROM
    acct_info acct
    left join org_info o
    on o. ID = acct.o_info_id
    WHERE
    1 = 1
    AND acct.is_delete = 0
     AND acct.ENT_ID = #{entId} 

     acct.LOGIN_SOURCE = #{loginSource}, 

     AND acct.ACCT_TITLE LIKE '%' || #{acctTitle} ||'%'

     AND acct.ACCT_PASSWORD = #{acctPassword} 

     AND acct.ACCT_CODE = #{acctCode} 

     AND acct.NAME LIKE '%' || #{name} ||'%'

     AND o.org_name LIKE '%' || #{orgTitle} ||'%'

     AND acct.DEPT = #{dept} 

     AND acct.MOBILE_PHONE = #{mobilePhone} 

     AND acct.EMAIL = #{email} 

     AND acct.REGISTER_TIME = #{registerTime} 

     AND acct.LAST_LOGIN_TIME = #{lastLoginTime} 

     AND acct.IS_DELETE = #{isDelete} 

     AND acct.UPDATED_BY = #{updatedBy} 

     AND acct.UPDATED_TIME = #{updatedTime} 

     AND acct.STATUS = #{status} 

     AND acct.START_TIME = #{startTime} 

     AND acct.ENT_TIME = #{entTime} 

     AND acct.A_COMPANY_ID = #{aCompanyId} 

     AND acct.O_INFO_ID like CONCAT(#{oInfoId},'%') 
    

     AND O_INFO_NAME = #{oInfoName} 

     AND acct.ACCT_TYPE = #{acctType} 
    order by acct.acct_id desc
