SELECT f1.*, CASE nullif( ( SELECT nullif(f.FUNCTION_ID, 0) FROM function_manage f LEFT JOIN acct_oper_priv_rela ao ON ao.FUNCTION_ID = f.function_id LEFT JOIN role_info ri ON ri.ROLE_ID = ao.ROLE_ID WHERE f. STATUS = 1
AND ri.role_id = #{roleId}
AND f1.function_id = f.function_id ), 0 ) WHEN 0 THEN 0 ELSE 1 END AS STATUS FROM function_manage f1 WHERE f1.PARENT_APPLICATION_ID IS NULL AND f1. STATUS = 1 AND f1.IS_DELETE = 0
AND f1.ORG_ID = #{orgId}

SELECT f1.*, CASE nullif( ( SELECT nullif(f.FUNCTION_ID, 0) FROM function_manage f LEFT JOIN acct_oper_priv_rela ao ON ao.FUNCTION_ID = f.function_id LEFT JOIN role_info ri ON ri.ROLE_ID = ao.ROLE_ID WHERE f. STATUS = 1
AND ri.role_id = #{roleId}
AND f1.function_id = f.function_id ), 0 ) WHEN 0 THEN 0 ELSE 1 END AS STATUS FROM function_manage f1 WHERE f1. STATUS = 1 AND f1. STATUS = 1 AND f1.IS_DELETE = 0
AND f1.PARENT_APPLICATION_ID = #{parentApplicationId}
AND f1.ORG_ID = #{orgId}

