SELECT D.FUNCTION_ID, D.FUNCTION_DESC, D.ID_FULL_PATH, D.TITLE_FULL_PATH, D.IS_LAST_LEVEL, D.PARENT_APPLICATION_ID, D.STATUS, D.CREATED_BY, D.CREATED_TIME, D.UPDATED_BY, D.UPDATED_TIME,D.FUNCTION_PATH FROM ACCT_INFO A LEFT JOIN ACCT_ROLE_REAL b ON A .ACCT_ID = b.ACCT_ID LEFT JOIN ACCT_OPER_PRIV_RELA C ON B.ROLE_ID = C.ROLE_ID LEFT JOIN FUNCTION_MANAGE D ON C.FUNCTION_ID = D.FUNCTION_ID where 1=1 and d.IS_DELETE=0
AND d.FUNCTION_TITLE = #{functionTitle}
AND d.ORG_ID = #{orgId}
AND a.ACCT_ID = #{acctId}
