select IFNULL(c.orAcct, 0) as orAcct,c.role_id,c.role_title from (select ( SELECT CASE IFNULL(ai.ACCT_ID, 0) when 0 then 0 else 1 end FROM role_info ri LEFT JOIN acct_role_real ar ON ar.ROLE_ID = ri.ROLE_ID LEFT JOIN acct_info ai ON ai.ACCT_ID = ar.ACCT_ID WHERE ri.STATUS = 1 and ri.IS_DELETE = 0
AND ri.ORG_ID = #{orgId}
AND ai.acct_id = #{acctId}
and ri.role_id=c.role_id )as orAcct,c.role_id,rg.ROLE_GROUP_TITLE,CONCAT ( ri.role_title, CASE WHEN rg.ROLE_GROUP_TITLE IS NULL THEN '' ELSE
'('|| rg.ROLE_GROUP_TITLE ||')'
END ) AS role_title from role_info c LEFT JOIN acct_role_group_role ar on ar.ROLE_ID=c.ROLE_ID LEFT JOIN role_group rg on rg.ROLE_GROUP_ID=ar.ROLE_GROUP_ID WHERE 1=1
AND c.ORG_ID = #{orgId}
and c.IS_DELETE = 0 ) as c
