SELECT h.* from(SELECT A.SID, A.CLAIM_NO, A.CLAIM_STATE, A.MILL_SHEET_NO, A.PRODUCT_ID, A.CUSTOMER_ID, A.CUSTOMER_NAME, A.BATTEN_PLATE_NO, A.PRODUCT_NAME, A.DESIGNATION, A.SIZE_MARK, C.CLAIM_TYPE, A.OBJECTION_NUM, C.INQUIRE_STATE, A.LAST_USER, A.PRESENTATION_DATE, A.REJECT_REASON, A.REASONS_FOR_COMPULSORY_CLOSURE, A.ADMISSIBILITY_TIME, (select name from ACCT_INFO where ACCT_ID = A.ADMISSIBILITY_USER) AS ADMISSIBILITY_USER, (select name from ACCT_INFO where ACCT_ID = A.PRESENTATION_USER) AS PRESENTATION_USER, (select MOBILE_PHONE from ACCT_INFO where ACCT_ID = A.ADMISSIBILITY_USER) AS PHONE, B.DEPT_CODE, (select T.TYPE_NAME from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=D.ZCPMC)) AS ZCPMC, (select T.SID from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=D.ZCPMC)) AS PARENT_SID, D.ZPH FROM WARRANTY.CRM_CLAIM_APPLY A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO LEFT JOIN WARRANTY.CRM_CLAIM_INFO C ON A.CLAIM_NO = C.CLAIM_NO LEFT JOIN WARRANTY.MILL_SHEET_HEAD D ON A.MILL_SHEET_NO = D.MILL_SHEET_NO ) H
AND TO_CHAR (H.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (H.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND H.MILL_SHEET_NO in
#{millSheetNos[${index}]}
AND H.CUSTOMER_NAME = #{customerId,jdbcType=VARCHAR}
AND H.PARENT_SID = #{sid,jdbcType=VARCHAR}
AND H.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
AND H.PRODUCT_ID = #{productId,jdbcType=NUMERIC}
AND H.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
AND H.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
AND H.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
AND H.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
AND H.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND H.DEPT_CODE in
#{deptCodes[${index}]}
AND H.DESIGNATION = #{designation,jdbcType=VARCHAR}
AND H.CLAIM_STATE = #{claimState}
order by H.SID desc

