SELECT * FROM ( SELECT A.CLAIM_STATE, B.DEPT_CODE FROM CRM_CLAIM_APPLY A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A .MILL_SHEET_NO = B.MILL_SHEET_NO
AND A.MILL_SHEET_NO in
#{millSheetNos[${index}]}
AND A.CUSTOMER_NAME = #{customerId,jdbcType=VARCHAR}
and A.CLAIM_NO LIKE '%' || #{claimNo} ||'%'
AND B.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND B.DEPT_CODE in
#{deptCodes[${index}]}
) PIVOT ( COUNT (CLAIM_STATE) FOR CLAIM_STATE IN ( 'NEW' as CREATED, 'PRESENT'as PRESENT, 'ACCEPTANCE' as ACCEPTANCE, 'REJECT' as REJECT, 'INVESTIGATION' as INVESTIGATION, 'HANDLE' as HANDLE, 'END' as END, 'ADOPT' as ADOPT, 'EVALUATE'as EVALUATE ) )

SELECT A.CLAIM_NO, A.CLAIM_STATE, A.MILL_SHEET_NO, A.PRODUCT_ID, A.PRODUCT_NAME, A.DESIGNATION, A.SIZE_MARK, A.ORDER_NO, C.CLAIM_TYPE, A.OBJECTION_NUM, C.INQUIRE_STATE, A.LAST_USER, A.CUSTOMER_NAME, A.PRESENTATION_DATE, A.REJECT_REASON, A.REASONS_FOR_COMPULSORY_CLOSURE, A.LAST_USER_ID, A.FILE_PATH, A.REPORT_PICTURE,
D.SPILT_CUSTOMER,
case when B.DEPT_CODE = '1000' then '不锈' when B.DEPT_CODE = '2000' then '炼轧' when B.DEPT_CODE = '2200' then '碳钢' when B.DEPT_CODE='3000' then '榆钢' else B.DEPT_CODE end as DEPT_CODE, (select T.TYPE_NAME from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=D.ZCPMC)) AS ZCPMC, D.ZPH FROM WARRANTY.CRM_CLAIM_APPLY A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO LEFT JOIN WARRANTY.CRM_CLAIM_INFO C ON A.CLAIM_NO = C.CLAIM_NO LEFT JOIN WARRANTY.MILL_SHEET_HEAD D ON A.MILL_SHEET_NO = D.MILL_SHEET_NO
AND TO_CHAR (A.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (A.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND A.CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
AND C.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
AND A.PRODUCT_ID = #{productId,jdbcType=VARCHAR}
AND A.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
AND A.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
AND A.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
AND A.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
AND B.DEPT_CODE = #{manufactor,jdbcType=VARCHAR}
AND A.DESIGNATION = #{designation,jdbcType=VARCHAR}
AND D.SPILT_CUSTOMER = #{orgName}
and a.CLAIM_STATE = #{claimState}
and a.CLAIM_STATE IN ('NEW','PRESENT','ACCEPTANCE','REJECT')

