SELECT
A.CLAIM_NO,
A.CLAIM_STATE,
A.MILL_SHEET_NO,
B.DEPT_CODE,
A.PRODUCT_ID,
A.PRODUCT_NAME,
A.DESIGNATION,
A.SIZE_MARK,
A.CLAIM_TYPE,
A.OBJECTION_NUM,
A.INQUIRE_STATE,
A.LAST_USER,
A.CUSTOMER_NAME,
A.PRESENTATION_DATE,
A.PRESENTATION_USER
FROM
WARRANTY.CRM_CLAIM_APPLY A
LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO

  
    AND TO_CHAR (A.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
    
  
  
    AND A.CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
  
  
    AND TO_CHAR (A.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
    
  
  
    AND A.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
  
  
    AND A.PRODUCT_ID = #{productId,jdbcType=VARCHAR}
  
  
    AND A.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
  
  
    AND A.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
  
  
    AND  B.DEPT_CODE = #{manufactor,jdbcType=VARCHAR}

SELECT
A.CLAIM_NO,
A.CLAIM_STATE,
A.MILL_SHEET_NO,
B.DEPT_CODE,
A.PRODUCT_ID,
A.PRODUCT_NAME,
A.DESIGNATION,
A.SIZE_MARK,
A.CLAIM_TYPE,
A.OBJECTION_NUM,
A.INQUIRE_STATE,
A.LAST_USER,
A.CUSTOMER_NAME,
D.PRESENTATION_DATE,
D.PRESENTATION_USER
FROM
WARRANTY.CRM_CLAIM_INFO A
LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO
LEFT JOIN WARRANTY.CRM_CLAIM_APPLY D ON A.CLAIM_NO = D.CLAIM_NO

  
    AND TO_CHAR (D.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
    
  
  
    AND TO_CHAR (D.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
    
  
  
    AND A.CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
  
  
    AND A.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
  
  
    AND A.PRODUCT_ID = #{productId,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
  
  
    AND A.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
  
  
    AND  B.DEPT_CODE = #{manufactor,jdbcType=VARCHAR}
  
  
    AND A.DESIGNATION = #{designation,jdbcType=VARCHAR}

SELECT
A.*,
B.DEPT_CODE,
D.PRESENTATION_DATE,
D.PRESENTATION_USER
FROM
WARRANTY.CRM_CLAIM_INFO A
LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO
LEFT JOIN WARRANTY.CRM_CLAIM_APPLY D ON A.CLAIM_NO = D.CLAIM_NO

  
  AND TO_CHAR (D.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
  


  AND A.CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}


  AND TO_CHAR (D.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
  

  
    AND A.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
  
  
    AND A.PRODUCT_ID = #{productId,jdbcType=VARCHAR}
  
  
    AND A.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
  
  
    AND A.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
  
  
    AND A.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
  
  
    AND  B.DEPT_CODE = #{manufactor,jdbcType=VARCHAR}
