mapper.select:
SELECT H.* FROM(SELECT A.BATTEN_PLATE_NO, A.SID, A.CLAIM_NO, A.CLAIM_STATE, A.MILL_SHEET_NO, A.CUSTOMER_NAME, A.REJECT_REASON, B.DEPT_CODE, A.PRODUCT_ID, A.PRODUCT_NAME, A.DESIGNATION, A.SIZE_MARK, A.CLAIM_TYPE, A.OBJECTION_NUM, C.PRESENTATION_DATE, C.ADMISSIBILITY_TIME, A.INQUIRE_STATE, D.CREATE_BY AS EXTERNAL_LNVESTIGATOR, D.CREATE_DT AS EXTERNAL_LNVESTIGATION_DATE, D.FOLLOW_REASON, E.CREATE_BY AS INTERNAL_LNVESTIGATOR, E.CREATE_DT AS INTERNAL_LNVESTIGATION_DATE,
D.LOGISTICS_PROCESS, D.END_PROCESSING_TECH, D.INQUIRE_INFO, A.LAST_USER_ADDR, D.DEFECT_NAME, D.PRODUCT_DT, D.USER_REQUIREMENT, D.FIELD_CONCLUSION_TEXT, D.HANDING_SUGGESTION, D.SALES_MANAGER_SUGGESTS, C.OBJECTION_CONFIRMATION, F.ZHTH, A.CONTRACT_NO,
(select T.TYPE_NAME from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=F.ZCPMC)) AS ZCPMC, (select T.SID from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=F.ZCPMC)) AS PARENT_SID, F.ZPH FROM WARRANTY.CRM_CLAIM_INFO A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO LEFT JOIN WARRANTY.CRM_CLAIM_APPLY C ON A.CLAIM_NO = C.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_OUT_INQUIRE D ON A.CLAIM_NO = D.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_INNER_INQUIRE E ON A.CLAIM_NO = E.CLAIM_NO LEFT JOIN WARRANTY.MILL_SHEET_HEAD F ON A.MILL_SHEET_NO = F.MILL_SHEET_NO ) H
AND TO_CHAR (H.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (H.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND H.CUSTOMER_NAME = #{customerId,jdbcType=VARCHAR}
AND H.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
AND H.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
AND H.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
AND H.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
and H.CLAIM_STATE = #{claimState1,jdbcType=VARCHAR}
AND H.CLAIM_STATE IN ('ADOPT','ACCEPTANCE','INVESTIGATION', 'HANDLE', 'END', 'EVALUATE','ADOPT')
AND H.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND H.DEPT_CODE in
#{deptCodes[${index}]}
AND H.PARENT_SID = #{sid,jdbcType=VARCHAR}
AND H.DESIGNATION = #{designation,jdbcType=VARCHAR}
AND H.INQUIRE_STATE = #{inquireState,jdbcType=VARCHAR}
order by H.SID desc

SELECT H.* FROM(SELECT A.CLAIM_NO, A.CLAIM_STATE, A.MILL_SHEET_NO, A.CUSTOMER_NAME, B.DEPT_CODE, A.PRODUCT_ID, A.PRODUCT_NAME, A.DESIGNATION, A.SIZE_MARK, A.CLAIM_TYPE, A.OBJECTION_NUM, C.PRESENTATION_DATE, C.ADMISSIBILITY_TIME, A.INQUIRE_STATE, D.CREATE_BY AS EXTERNAL_LNVESTIGATOR, D.CREATE_DT AS EXTERNAL_LNVESTIGATION_DATE, D.FOLLOW_REASON, E.CREATE_BY AS INTERNAL_LNVESTIGATOR, E.CREATE_DT AS INTERNAL_LNVESTIGATION_DATE, (select T.TYPE_NAME from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=E.ZCPMC)) AS ZCPMC, (select T.PARENT_SID from sgai_md_common_type t WHERE T.SID=(select T.PARENT_SID from sgai_md_common_type t WHERE T.TYPE_NAME=E.ZCPMC)) AS PARENT_SID, E.ZPH FROM WARRANTY.CRM_CLAIM_INFO A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO LEFT JOIN WARRANTY.CRM_CLAIM_APPLY C ON A.CLAIM_NO = C.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_OUT_INQUIRE D ON A.CLAIM_NO = D.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_INNER_INQUIRE E ON A.CLAIM_NO = E.CLAIM_NO LEFT JOIN WARRANTY.MILL_SHEET_HEAD E ON A.MILL_SHEET_NO = E.MILL_SHEET_NO ) H
AND TO_CHAR (H.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (H.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND H.CUSTOMER_NAME = #{customerId,jdbcType=VARCHAR}
AND H.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
AND H.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
AND H.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
AND H.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
and H.CLAIM_STATE = #{claimState1,jdbcType=VARCHAR}
AND H.CLAIM_STATE IN ('ADOPT','ACCEPTANCE','INVESTIGATION', 'HANDLE', 'END', 'EVALUATE','ADOPT')
AND H.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND H.PARENT_SID = #{sid,jdbcType=VARCHAR}
AND H.DESIGNATION = #{designation,jdbcType=VARCHAR}
AND H.INQUIRE_STATE = #{inquireState,jdbcType=VARCHAR}
