SELECT A.CLAIM_NO, A.CLAIM_STATE, A.MILL_SHEET_NO, B.DEPT_CODE, A.PRODUCT_ID, A.PRODUCT_NAME, A.DESIGNATION, A.SIZE_MARK, A.CLAIM_TYPE, A.OBJECTION_NUM, C.PRESENTATION_DATE, C.ADMISSIBILITY_TIME, D.INQUIRE_STATE, D.CREATE_BY AS EXTERNAL_LNVESTIGATOR, D.CREATE_DT AS EXTERNAL_LNVESTIGATION_DATE, D.FOLLOW_REASON, E.CREATE_BY AS INTERNAL_LNVESTIGATOR, E.CREATE_DT AS INTERNAL_LNVESTIGATION_DATE FROM WARRANTY.CRM_CLAIM_INFO A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.MILL_SHEET_NO = B.MILL_SHEET_NO LEFT JOIN WARRANTY.CRM_CLAIM_APPLY C ON A.CLAIM_NO = C.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_OUT_INQUIRE D ON A.CLAIM_NO = D.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_INNER_INQUIRE E ON A.CLAIM_NO = E.CLAIM_NO
AND TO_CHAR (C.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (C.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND A.CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
AND A.CLAIM_TYPE = #{claimType,jdbcType=VARCHAR}
AND A.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
AND A.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
AND A.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
AND A.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
AND B.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND A.DESIGNATION = #{designation,jdbcType=VARCHAR}

SELECT A.CLAIM_NO, A.CLAIM_STATE, A.MILL_SHEET_NO, B.DEPT_CODE, A.PRODUCT_ID, A.PRODUCT_NAME, A.DESIGNATION, A.SIZE_MARK, C.CLAIM_TYPE, A.OBJECTION_NUM, A.PRESENTATION_DATE, A.ADMISSIBILITY_TIME, D.INQUIRE_STATE, D.CREATE_BY AS EXTERNAL_LNVESTIGATOR, D.CREATE_DT AS EXTERNAL_LNVESTIGATION_DATE, D.FOLLOW_REASON, E.CREATE_BY AS INTERNAL_LNVESTIGATOR, E.CREATE_DT AS INTERNAL_LNVESTIGATION_DATE FROM WARRANTY.CRM_CLAIM_APPLY A LEFT JOIN WARRANTY.MILL_SHEET_HOSTS B ON A.CLAIM_NO = B.MILL_SHEET_NO LEFT JOIN WARRANTY.CRM_CLAIM_INFO C ON A.CLAIM_NO = C.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_OUT_INQUIRE D ON A.CLAIM_NO = D.CLAIM_NO LEFT JOIN WARRANTY.CRM_CLAIM_INNER_INQUIRE E ON A.CLAIM_NO = E.CLAIM_NO
AND TO_CHAR (A.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND A.CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
AND TO_CHAR (A.PRESENTATION_DATE, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND A.DESIGNATION = #{designation,jdbcType=VARCHAR}
AND A.PRODUCT_NAME = #{productName,jdbcType=VARCHAR}
AND A.CLAIM_NO = #{claimNo,jdbcType=VARCHAR}
AND A.BATTEN_PLATE_NO = #{battenPlateNo,jdbcType=VARCHAR}
AND A.CLAIM_STATE = #{claimState,jdbcType=VARCHAR}
