mapper.select:
select

from CRM_PRODUCT_INFO

  
    AND PRODUCT_NAME LIKE '%' || #{productName} ||'%'
  
  
    AND USED LIKE '%' || #{used} ||'%'
  
  
    AND status = #{status}
  
  
    AND category_Id = #{categoryId}
  
  
    and FACTORY in
    
      #{factoryCodes[${index}]}
    
  

order by SORT_NUMBER desc

select
*
from CRM_PRODUCT_INFO

  
    AND PRODUCT_NAME LIKE '%' || #{productName} ||'%'
  
  
    AND USED LIKE '%' || #{used} ||'%'
  
  
    AND status = #{status}
  
  
    AND category_Id = #{categoryId}
  
  
    AND FACTORY = #{factory}
  

order by SORT_NUMBER desc

SELECT A.PID, A.PRODUCT_NAME, A.DESIGNATION, A.STANDARD, A.CATEGORY_ID, A.USED, A.THUMBNAIL, A.PRODUCT_DESC, A.PRODUCT_MANUAL, A.SORT_NUMBER, A.PRODUCT_AREA, A.STATUS, A.CREATE_BYID, A.CREATE_BY, A.CREATE_DT, A.UPDATE_BYID, A.UPDATE_BY, A.UPDATE_DT, A.FACTORY, B.CATEGORY_NAME FROM CRM_PRODUCT_INFO A LEFT JOIN CRM_PRODUCT_CATEGORY b ON A .CATEGORY_ID = b.cid
AND A.PRODUCT_NAME = #{productName}
AND A.DESIGNATION = #{designation}
AND A.STANDARD = #{standard}
AND A.PRODUCT_AREA = #{productArea}
AND A.CATEGORY_ID = #{categoryId}
