mapper.select:
select

from CRM_QUESTION

  
    AND QUESTION_TITLE  LIKE '%' || #{questionTitle} ||'%'
  
  
    AND PUSH_REGION  LIKE '%' || #{pushRegion} ||'%'
  
  
    AND QUESTION_STATUS = #{questionStatus}
  
  
    AND TO_CHAR (CREATE_DT, 'yyyy-MM-dd hh24:mi:ss') >= #{createDtStr}
  
  
    AND TO_CHAR (CREATE_DT, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
  

order by qid desc

select

from CRM_QUESTION

  
    AND QUESTION_TITLE  LIKE '%' || #{questionTitle} ||'%'
  
  
    AND PUSH_REGION  LIKE '%' || #{pushRegion} ||'%'
  
  
    AND QUESTION_STATUS = #{questionStatus}
  
  
    AND TO_CHAR (CREATE_DT, 'yyyy-MM-dd hh24:mi:ss') <= #{createDtStr}
  
  
    AND TO_CHAR (END_DT, 'yyyy-MM-dd hh24:mi:ss') >= #{endDtStr}
  

order by qid desc

select
count(*)
from CRM_QUESTION

  
    AND QUESTION_TITLE  LIKE '%' || #{questionTitle} ||'%'
  
  
    AND PUSH_REGION  LIKE '%' || #{pushRegion} ||'%'
  
  
    AND QUESTION_STATUS = #{questionStatus}
  
  
    AND TO_CHAR (CREATE_DT, 'yyyy-MM-dd hh24:mi:ss') >= #{createDtStr}
  
  
    AND TO_CHAR (CREATE_DT, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
  

order by qid desc
