mapper.select:
SELECT msh.MILL_SHEET_NO, msh.STATE, mci.ZCHARG, mci.SPECS, mci.ZLOSMENGE,mci.ZJISHU, mpd.SID AS MPD_SID, mpd.KURZTEXT AS MPD_KURZTEXT, mpd.ORIGINAL_INPUT as MPD_ORIGINAL_INPUT FROM MILL_SHEET_HOSTS msh LEFT JOIN MILL_COIL_INFO mci on msh.MILL_SHEET_NO = mci.MILL_SHEET_NO LEFT JOIN MILL_PHYSICS_DATA mpd on mci.ZCHARG = mpd.ZCHARG AND mpd.MILL_SHEET_NO=msh.MILL_SHEET_NO
and msh.MILL_SHEET_NO = #{millSheetNo}
and mpd.SHOW_FLAG = #{showFlag}

SELECT msh.MILL_SHEET_NO, msh.STATE, mci.ZCHARG, mci.SPECS, mci.ZLOSMENGE,mci.ZJISHU, mcd.SID AS MCD_SID, mcd.KURZTEXT AS MCD_KURZTEXT, mcd.ORIGINAL_INPUT AS MCD_ORIGINAL_INPUT FROM MILL_SHEET_HOSTS msh LEFT JOIN MILL_COIL_INFO mci on msh.MILL_SHEET_NO = mci.MILL_SHEET_NO LEFT JOIN MILL_CHEMISTRY_DATA mcd on mci.ZCHARG = mcd.ZCHARG AND mcd.MILL_SHEET_NO=msh.MILL_SHEET_NO
and msh.MILL_SHEET_NO = #{millSheetNo}
and mcd.SHOW_FLAG = #{showFlag}

SELECT mci.SID, mci.ZCHEHAO, mci.ZLPH, mci.ZCHARG, mci.SPECS, mci.ZDIAMETER, mci.ZTHICK, mci.ZWIDTH, mci.ZLENGTH, mci.ZJISHU, mci.ZLOSMENGE, mci.ZJISHU_UNIT, mci.ZMEINS, mci.ZDCZT, mci.MILL_SHEET_NO, mci.ZBMJD, mci.ZBZFS, mci.ZTSBZ, mci.ZHDPC, mci.CREATED_BY, mci.CREATED_DT, mci.UPDATED_BY, mci.UPDATED_DT, mci.VERSION FROM MILL_COIL_INFO mci LEFT JOIN MILL_SHEET_HEAD msh ON mci.MILL_SHEET_NO = msh.MILL_SHEET_NO WHERE mci. MILL_SHEET_NO IN ( SELECT DISTINCT MILL_SHEET_NO FROM MILL_SHEET_HEAD
AND ZKUNNR = #{zkunnr}
AND ZCHARG = #{zcharg}
)

SELECT DISTINCT mci.ZCHARG, mci.SPECS, mpd.ORIGINAL_INPUT, mpd.KURZTEXT AS MPD_KURZTEXT FROM MILL_COIL_INFO mci LEFT JOIN MILL_PHYSICS_DATA mpd ON mci.ZCHARG = mpd.ZCHARG AND mci.MILL_SHEET_NO = mpd.MILL_SHEET_NO
AND mci.SID = #{sid}
AND mci.ZCHARG = #{zcharg}
AND mpd.SHOW_FLAG = #{showFlag}

    SELECT
    DISTINCT mci.ZCHARG, mci.SPECS, mci.ZLOSMENGE,
    mcd.SID AS MCD_SID, mcd.KURZTEXT AS MCD_KURZTEXT, mcd.ORIGINAL_INPUT AS MCD_ORIGINAL_INPUT
    FROM MILL_COIL_INFO mci
    LEFT JOIN MILL_CHEMISTRY_DATA mcd on mci.ZCHARG = mcd.ZCHARG AND mcd.MILL_SHEET_NO=mci.MILL_SHEET_NO
    
      
        AND mci.SID = #{sid}
      
      
        AND mci.ZCHARG = #{zcharg}
      
      
        AND mcd.SHOW_FLAG = #{showFlag}

SELECT msd.MILL_SHEET_NO, msd.ZKUNWE, msd.ZCPMC, msd.ZPH, msd.ZZXBZ, msd.ZCHEHAO, msd.ZDAOZHAN, msd.ZJHZT, mci.ZCHARG, mci.SPECS, mci.ZLOSMENGE, mci.ZJISHU, mci.ZLPH, mcd. SID AS MCD_SID, mcd.KURZTEXT AS MCD_KURZTEXT, mcd.ORIGINAL_INPUT AS MCD_ORIGINAL_INPUT, mcd.SHOW_FLAG, (select distinct name1 from MILL_CUSTOMER where KUNNR = msd.ZKUNWE) AS NAME1 FROM MILL_SHEET_HEAD msd LEFT JOIN MILL_COIL_INFO mci ON msd.MILL_SHEET_NO = mci.MILL_SHEET_NO LEFT JOIN MILL_CHEMISTRY_DATA mcd ON mci.ZCHARG = mcd.ZCHARG AND msd.MILL_SHEET_NO = mcd.MILL_SHEET_NO and mcd.SPECS = mci.SPECS
and msd.MILL_SHEET_NO = #{millSheetNo}
and mcd.SHOW_FLAG = #{showFlag}
ORDER BY CASE WHEN MCD_KURZTEXT = 'C'then 1 WHEN MCD_KURZTEXT = 'Si'then 2 WHEN MCD_KURZTEXT = 'Mn'then 3 WHEN MCD_KURZTEXT = 'P'then 4 WHEN MCD_KURZTEXT = 'S'then 5 END

SELECT msd.MILL_SHEET_NO, msd.ZKUNWE, msd.ZCPMC, msd.ZPH, msd.ZZXBZ, msd.ZCHEHAO, msd.ZDAOZHAN, msd.ZJHZT, mci.ZCHARG, mci.SPECS, mci.ZLOSMENGE, mci.ZJISHU, mci.ZLPH, mpd. SID AS MPD_SID, mpd.KURZTEXT AS MPD_KURZTEXT, mpd.ORIGINAL_INPUT AS MPD_ORIGINAL_INPUT, ( SELECT DISTINCT name1 FROM MILL_CUSTOMER WHERE KUNNR = msd.ZKUNWE ) AS NAME1 FROM MILL_SHEET_HEAD msd LEFT JOIN MILL_COIL_INFO mci ON msd.MILL_SHEET_NO = mci.MILL_SHEET_NO LEFT JOIN MILL_PHYSICS_DATA mpd ON mpd.ZCHARG = mci.ZCHARG AND msd.MILL_SHEET_NO = mpd.MILL_SHEET_NO AND mpd.SPECS = mci.SPECS
and msd.MILL_SHEET_NO = #{millSheetNo}
and mpd.SHOW_FLAG = #{showFlag}

	SELECT SPECS , ZLOSMENGE
	FROM MILL_COIL_INFO
    
      
        and ZCHARG = #{zcharg}
      
      
        and MILL_SHEET_NO = #{millSheetNo}

SELECT MILL_SHEET_NO,
        ZCHARG,
        SPECS,
        SURPLUS_ZLOSMENGE AS ZLOSMENGE,
        SURPLUS_ZJISHU AS ZJISHU
FROM MILL_COIL_INFO
WHERE
MILL_SHEET_NO = #{millSheetNo}
    and ZCHARG = #{zcharg}
    and SPECS = #{specs}

mapper.update:
update MILL_COIL_INFO

  set version = version + 1,
  
    SURPLUS_ZJISHU = #{zjishu,jdbcType=DECIMAL},
  
  
    SURPLUS_ZLOSMENGE = #{zlosmenge,jdbcType=DECIMAL},
  
  
    UPDATED_BY = #{updatedBy,jdbcType=VARCHAR},
  
  
    UPDATED_DT = #{updatedDt,jdbcType=TIMESTAMP},
  

WHERE
MILL_SHEET_NO = #{millSheetNo}
and ZCHARG = #{zcharg}
and SPECS = #{specs}
