mapper.select:
SELECT MFI.FALLBACK_REASON, case when MFS.STEP_RESULT ='Y' then '' else MFI.REJECTION_REASON end as REJECTION_REASON, MSO.STATE, MSO.DATA_STATE, MSO.MILL_SHEET_NO_OLD, MSO.MILL_SHEET_NO, MSO.SPECIAL_NEED, MSO.PRINTABLE_NUM, MSO.PRINTED_NUM, MSO.MILL_SHEET_URL, MSO.MILL_SHEET_NAME, MSO.DEPT_CODE, MSO.DOWNABLE_NUM, MSO.DOWN_NUM, MSO.HAVE_STATE, MSO.SPLIT_STATE, MSO.MILL_SHEET_TYPE, MSO.SURPLUS_BATCH, MSO.ZKUNWE, MSO.JC_FLAG, MSH.ZCHEHAO, MSH.ZHTH, MSH.ZCPMC, MSH.LFDAT, MSH.SPILT_CUSTOMER, MSH.ZPH, MSH.MILL_LINE, MSH.ZKDAUF AS ZKDAUF, MSO.SID, (SELECT sum(ZJISHU) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as ZJISHU, (SELECT sum(ZLOSMENGE) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as ZLOSMENGE, (SELECT sum(SURPLUS_ZJISHU) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as SURPLUS_ZJISHU, (SELECT sum(SURPLUS_ZLOSMENGE) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as SURPLUS_ZLOSMENGE, (select distinct name1 from MILL_CUSTOMER where KUNNR = MSO.ZKUNNR) AS ZKUNNR FROM WARRANTY.MILL_SHEET_HOSTS MSO LEFT JOIN WARRANTY.MILL_SHEET_HEAD MSH ON MSO.MILL_SHEET_NO = MSH.MILL_SHEET_NO LEFT JOIN WARRANTY.MILL_FALLBACK_INFO MFI ON MSO.MILL_SHEET_NO = MFI.MILL_SHEET_NO LEFT JOIN WARRANTY.MILL_FALLBACK_STEPS MFS ON MSO.MILL_SHEET_NO = MFS.MILL_SHEET_NO
AND TO_CHAR (MSH.LFDAT, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (MSh.LFDAT, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND MSO.MILL_SHEET_NO = #{millSheetNo,jdbcType=VARCHAR}
AND MSO.MILL_SHEET_NO in
#{millSheetNos[${index}]}
AND MSO.MILL_SHEET_NO NOT in
#{noMillSheetNos[${index}]}
AND MSH.ZCHEHAO like '%' || #{zchehao,jdbcType=VARCHAR} || '%'
AND MSH.ZHTH = #{zhth,jdbcType=VARCHAR}
AND MSH.SPILT_CUSTOMER = #{zkunnr,jdbcType=VARCHAR}
AND MSH.ZPH = #{zph,jdbcType=VARCHAR}
AND MSH.SPILT_CUSTOMER = #{spiltCustomer,jdbcType=VARCHAR}
AND MSH.SPILT_CUSTOMER = #{zkunnrs,jdbcType=VARCHAR}
AND MSO.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND MSO.DEPT_CODE in
#{deptCodes[${index}]}
AND MSH.MILL_LINE = #{millLine,jdbcType=VARCHAR}
and MSO.STATE = #{states,jdbcType=VARCHAR}
AND MSO.STATE IN ('EXAMINED', 'DOWNLOADED', 'PRIVIEWED', 'PRINTED','SPLITED')
AND MSH.SPILT_CUSTOMER = #{orgName}
AND MSO.MILL_SHEET_TYPE = #{millSheetType}
AND MSH.ZCHEHAO NOT LIKE '-%'
order by MSO.SID desc

select MILL_SHEET_URL , MILL_SHEET_NAME , DOWNABLE_NUM
from MILL_SHEET_HOSTS
where MILL_SHEET_NO = #{millSheetNo}

  
    and STATE  = #{states}
  
  
    AND STATE  IN ('EXAMINED', 'DOWNLOADED', 'PRIVIEWED')

SELECT H.*FROM(SELECT MFI.FALLBACK_REASON, case when MFS.STEP_RESULT ='Y' then '' else MFI.REJECTION_REASON end as REJECTION_REASON, MSO.STATE, MSO.DATA_STATE, MSO.MILL_SHEET_NO_OLD, MSO.MILL_SHEET_NO, MSO.SPECIAL_NEED, MSO.PRINTABLE_NUM, MSO.PRINTED_NUM, MSO.MILL_SHEET_URL, MSO.MILL_SHEET_NAME, MSO.DEPT_CODE, MSO.DOWNABLE_NUM, MSO.DOWN_NUM, MSO.HAVE_STATE, MSO.SPLIT_STATE, MSO.MILL_SHEET_TYPE, MSO.SURPLUS_BATCH, MSO.ZKUNWE, MSO.JC_FLAG, MSH.ZCHEHAO, MSH.ZHTH, MSH.ZCPMC, MSH.LFDAT, MSH.ZPH, MSH.ZDAOZHAN, MSH.MILL_LINE, MSH.ZKDAUF AS ZKDAUF, MSH.SPILT_CUSTOMER, MSO.SID, (SELECT sum(ZJISHU) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as ZJISHU, (SELECT sum(ZLOSMENGE) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as ZLOSMENGE, (SELECT sum(SURPLUS_ZJISHU) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as SURPLUS_ZJISHU, (SELECT sum(SURPLUS_ZLOSMENGE) FROM MILL_COIL_INFO WHERE MILL_SHEET_NO=MSH.MILL_SHEET_NO )as SURPLUS_ZLOSMENGE, (select distinct name1 from MILL_CUSTOMER where KUNNR = MSO.ZKUNNR) AS ZKUNNR FROM WARRANTY.MILL_SHEET_HOSTS MSO LEFT JOIN WARRANTY.MILL_SHEET_HEAD MSH ON MSO.MILL_SHEET_NO = MSH.MILL_SHEET_NO LEFT JOIN WARRANTY.MILL_FALLBACK_INFO MFI ON MSO.MILL_SHEET_NO = MFI.MILL_SHEET_NO LEFT JOIN WARRANTY.MILL_FALLBACK_STEPS MFS ON MSO.MILL_SHEET_NO = MFS.MILL_SHEET_NO) h
AND TO_CHAR (H.LFDAT, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (H.LFDAT, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND H.MILL_SHEET_NO like '%' || #{millSheetNo} ||'%'
AND H.MILL_SHEET_NO in
#{millSheetNos[${index}]}
AND H.MILL_SHEET_NO NOT in
#{noMillSheetNos[${index}]}
AND H.ZCHEHAO like '%' || #{zchehao,jdbcType=VARCHAR} || '%'
AND H.ZHTH = #{zhth,jdbcType=VARCHAR}
AND H.ZKUNNR = #{zkunnr,jdbcType=VARCHAR}
AND H.ZPH = #{zph,jdbcType=VARCHAR}
AND H.SPILT_CUSTOMER like '%' || #{zkunnrs} ||'%'
AND H.DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
AND H.MILL_SHEET_TYPE = #{millSheetType}
AND H.ZDAOZHAN = #{zdaozhan}
AND H.DEPT_CODE in
#{deptCodes[${index}]}
AND H.MILL_LINE like '%' || #{millLine} ||'%'
and H.STATE = #{states,jdbcType=VARCHAR}
AND H.STATE IN ('EXAMINED', 'DOWNLOADED', 'PRIVIEWED', 'PRINTED','SPLITED')
AND H.ZCHEHAO NOT LIKE '-%'
order by H.SID desc

    SELECT
    mss.STATE,
    msd.ZCPMC, msd.MILL_SHEET_NO, msd.ZPH, msd.ZKDAUF, msd.LFDAT
    FROM MILL_SHEET_HOSTS mss
    LEFT JOIN MILL_SHEET_HEAD msd ON MSD.MILL_SHEET_NO = MSS.MILL_SHEET_NO
    
      WHERE msd.MILL_SHEET_NO IN
      (
      SELECT
      mco.MILL_SHEET_NO
      FROM MILL_COIL_INFO mco
      WHERE mco.ZCHARG= #{zcharg}
      GROUP BY mco.MILL_SHEET_NO
      )
      
        AND msd.ZCHEHAO= #{zchehao}
      
      
        AND TO_CHAR (msd.LFDAT, 'yyyy-MM-dd') = #{lfdat}
      

      
        AND TO_CHAR (msd.LFDAT, 'yyyy-MM-dd') >= #{startDtStr}
      
      
        AND TO_CHAR (msd.LFDAT, 'yyyy-MM-dd') <= #{endDtStr}
      

      
        AND mss.MILL_SHEET_NO like  '%' || #{millSheetNo} ||'%'
      
      
        AND msd.SPILT_CUSTOMER = #{orgName}
      
    
    AND mss.STATE  IN ('EXAMINED', 'DOWNLOADED', 'PRIVIEWED', 'PRINTED','SPLITED')

    SELECT
    mss.STATE,
    msd.ZCPMC, msd.MILL_SHEET_NO, msd.ZPH, msd.ZKDAUF, msd.LFDAT
    FROM MILL_SHEET_HOSTS mss
    LEFT JOIN MILL_SHEET_HEAD msd ON MSD.MILL_SHEET_NO = MSS.MILL_SHEET_NO
    

      
        AND msd.ZCHEHAO= #{zchehao}
      
      
        AND TO_CHAR (msd.LFDAT, 'yyyy-MM-dd') = #{lfdat}
      

      
        AND TO_CHAR (msd.LFDAT, 'yyyy-MM-dd') >= #{startDtStr}
      
      
        AND TO_CHAR (msd.LFDAT, 'yyyy-MM-dd') <= #{endDtStr}
      

      
        AND mss.MILL_SHEET_NO like  '%' || #{millSheetNo} ||'%'
      
      
        AND msd.SPILT_CUSTOMER = #{orgName}
      

    
    AND mss.STATE  IN ('EXAMINED', 'DOWNLOADED', 'PRIVIEWED', 'PRINTED','SPLITED')

SELECT *
FROM MILL_SHEET_HEAD
WHERE MILL_SHEET_NO = #{millSheetNo}

SELECT *
FROM MILL_SHEET_HOSTS
WHERE MILL_SHEET_NO = #{MILLSHEETNO}
